mapper.select:
select b.*
from tb_equipment_buy b
where b.teacher_id = #{teacher_id}

mapper.insert:
insert into tb_equipment (`name`)
select #{name}
from DUAl
where not exists (
    select e.*
    from tb_equipment e
    where e.`name` = #{name}
)

mapper.select:
select b.`name`
from tb_equipment_buy b
where b.id = #{id}
